$(".owl-feedbacks").owlCarousel({
    items: 1,
    dots: true,
    nav: false,
    responsiveClass:true,
    responsive:{
        0:{
            items: 1
        },

        900:{
            items:2
        },
        1130:{
            items:3
        }
    }
});



    //при попытке отправить форму обратной связи - проверим, что все заполнено корректно
    $(".feedback .button").click(function(e){

        //проверяем, если у кнопки нет класса, что она выключена, то вообще ничего не делаем
        if (!$(this).hasClass("feedback__submit_disabled")){
            //идем по всем инпутам внутри формы
            $(".feedback__input").each(function(index, item) {
                //если пользователь забыл заполнить поле
                if ($(item).val() == ""){
                    //добавляем класс, сигнализирующий об ошибке
                    $(item).addClass("feedback__input_error");
                    //меняем placeholder данного input на "Поле не заполнено"
                    $(item).attr("placeholder", "Поле не заполнено");
                }
            })
        }
        else {
            e.preventDefault();
        }

    })

    //при получении фокуса инпутом из формы убираем у него класс ошибки ввода
    $(".feedback__input").focus(function(){
        $(this).removeClass("feedback__input_error");
    });

    //при изменении состояния чембокса о согласии на обработку персональных данных
    $("#feedback__agree").change(function() {
        if ($(this).is(":checked"))
            $(".feedback__submit").removeClass("feedback__submit_disabled");
        else
            $(".feedback__submit").addClass("feedback__submit_disabled");

    })


//при клике на пункт меню хедера, у которого есть вложенное подменю (для мобильных устройств)
$(".header nav .navbar-nav li").click(function() {
    //если ширина экрана как у мобильника и меньше
    if ($(window).width() < 750)
        //показать вложенное меню (так как у мобильных устройств отсутсвует понятие hover)
        $(this).find(".header__submenu").toggle(300);

});

//при наведении на пункт меню хедера, у которого есть вложенное подменю (для больших экранов)
$(".header nav .navbar-nav li").mouseover(function() {
        //если вложенное меню есть, оно выпадает (это описано в css), а тег main затемняется
        if ($(this).has(".header__submenu").length > 0){
            $(".gray-wrapper").show();
        }
});
// и наоборот, когда мышка покидает пункт меню хедера, у которого есть вложенное подменю (для больших экранов)
$(".header nav .navbar-nav li").mouseout(function() {
        //если вложенное меню есть, оно пропадает (это описано в css), а тег main теряет затемнение
        if ($(this).has(".header__submenu").length > 0){
            $(".gray-wrapper").hide();
        }
});

